[asy]
fill((0,2)..(2,0)--(-2,0)..cycle,gray(0.7));
fill((-1,1)..(0,0)--(-2,0)..cycle,white);
fill((1,1)..(0,0)--(2,0)..cycle,white);
fill((0,1)..(1,0)--(-1,0)..cycle,white);
draw((0,1)..(1,0)--(-1,0)..cycle,dashed);
draw((0,2)..(2,0)--(-2,0)..cycle);
label("$A$",(-2,0),W);
label("$B$",(2,0),E);
label("1",(-1.5,0),S);
label("2",(0,0),S);
label("1",(1.5,0),S);
dot((0,0));
dot((-1,0));
dot((1,0));
draw((-2,-0.1)--(-2,-0.4));
draw((-1,-0.1)--(-1,-0.4));
draw((2,-0.1)--(2,-0.4));
draw((1,-0.1)--(1,-0.4));
[/asy]

